import{_ as a}from"./new_tab_icon-23b55aad.js";import{r as s,o as c,c as l,a as e,b as o,e as t,w as f,d,u as m}from"./app-712dea73.js";const _="/code/assets/faqs_icon-b726745f.svg",b={id:"pageContent",class:"codehub faqs"},g={id:"printReady"},v=e("div",{class:"h1wrapper_outer"},[e("div",{class:"h1wrapper_inner"},[e("h1",{id:"codehub-page-heading"},[e("img",{style:{width:"42px",height:"auto","margin-right":"9px"},src:_,alt:"2 speech bubbles signifying a conversation"}),o("ITS CodeHub: Frequently Asked Questions ")])])],-1),S={class:"codehub-content"},w=e("p",{class:"faqs-question"},"What is ITS CodeHub?",-1),T=e("p",{class:"faqs-answer"}," ITS CodeHub provides a single point of entry to discover the U.S. Department of Transportation's (DOT) publicly available open source code funded by the Intelligent Transportation Systems (ITS) Joint Program Office (JPO). ",-1),I=e("p",{class:"faqs-question"},"How can I register my repositories on ITS CodeHub?",-1),y={class:"faqs-answer"},q=d('<p class="faqs-question"> Does ITS CodeHub have any guidelines for developing open source code? </p><p class="faqs-answer p_reduce-bottom-margin"> ITS CodeHub encourages safe coding practices and for code to be developed with open standards in mind. Here is the ITS JPO CodeHub&#39;s Code Quality Best Practices Checklist: </p><ul class="ul_remove-spacing_above-and-below"><li>No Usernames or Passwords</li><li>No Confidential Business Information (CBI)</li><li>No non-public internet protocol (IP) addresses</li><li> Code uses open standards from <a href="https://www.transportation.gov/sites/dot.gov/files/docs/mission/284471/policy-135127-enterprise-architecture-implementation-instruction-source-code-managementfinal.pdf" target="_blank" rel="noopener noreferrer">U.S. DOT Source Code Management<img src="'+a+'" alt="External link icon" class="external-link-icon inline"></a></li><li>Code uses Open Source Software (OSS) licenses</li><li>Obtain appropriate data rights to custom-developed code</li><li> Establish processes for the review and approval of all requests to release custom-developed code as OSS </li></ul>',3),C=e("img",{src:a,alt:"External link icon",class:"external-link-icon inline"},null,-1),k=d('<p class="faqs-answer"> For more information on coding practices, please see <a href="https://www.transportation.gov/digitalstrategy/policyarchive/open-source-management" target="_blank" rel="noopener noreferrer">U.S. DOT&#39;s Open Source Management<img src="'+a+'" alt="External link icon" class="external-link-icon inline"></a>. </p><p class="faqs-question"> May I freely use and distribute the open source code I discovered on ITS CodeHub? </p><p class="faqs-answer"> The open source code repositories discoverable on ITS CodeHub come with open source license information including terms for use. Check each repository&#39;s accompanying LICENSE and/or README file for additional information about use and reuse. </p><p class="faqs-question"> Is there a location where I can find transportation data to support the development and use of the source code discoverable through ITS CodeHub? </p><p class="faqs-answer"> Yes, <a href="https://its.dot.gov/data/" target="_blank" rel="noopener noreferrer">ITS Datahub<img src="'+a+'" alt="External link icon" class="external-link-icon inline"></a> provides a single point of entry to discover U.S. DOT&#39;s publicly available, reusable, and open ITS research data, including connected vehicle data. A wide variety of datasets are available on ITS DataHub including: connected vehicle messages, automated vehicle data, trajectories, field test data and connected equipment data. ITS DataHub is an excellent location to search for highly documented transportation data to support the source code found on ITS CodeHub. </p><p class="faqs-question">What does &quot;open source&quot; mean?</p><p class="faqs-answer"> Open source means the work is publicly accessible and can be used, modified, and shared by anyone. Open source works are distributed under licenses that comply with the definition provided by the Open Source Initiative. For additional information, please also see the Open License Guide (Link to Open License Guide). </p><p class="faqs-question">Who can I contact for additional information?</p><p class="faqs-answer"> For more information and any additional questions or concerns, please contact the ITS JPO Support Team at <a href="mailto:data.itsjpo@dot.gov">data.itsjpo@dot.gov</a>. </p>',9),O={__name:"Faqs",setup(p){const i="/code/documents/ITS_JPO_CodeHubs_Code_Quality_Best_Practices_Checklist.pdf";return(u,r)=>{const n=s("RouterLink");return c(),l("div",b,[e("div",g,[v,e("div",S,[w,T,I,e("p",y,[o(" Check out the detailed registration process on the "),t(n,{to:"/registration",rel:"noopener noreferrer"},{default:f(()=>[o("Repository Registration page")]),_:1}),o(". You will be asked to fill out and submit the linked form. Once complete, the ITS JPO Support Team will review your application and determine whether the repository will be made discoverable through ITS CodeHub. ")]),q,e("p",{class:"p_reduce-top-margin"},[e("a",{href:i,rel:"noopener noreferrer",target:"_blank"},[o("Download the ITS JPO CodeHub's Code Quality Best Practices Checklist"),C])]),k])])])}}},H={id:"searchTerms"},P={__name:"FaqsView",setup(p){return m({title:"Department of Transportation - ITS CodeHub | FAQs"}),(i,u)=>{const r=s("HeaderDOT"),n=s("Navigation"),h=s("FooterDOT");return c(),l("div",H,[e("header",null,[t(r)]),e("nav",null,[t(n)]),e("main",null,[t(O)]),e("footer",null,[t(h)])])}}};export{P as default};
